<template>
  <div>
    <a-form class="smart-query-form">
      <a-row class="smart-query-form-row">
        <a-form-item label="姓名" class="smart-query-form-item">
          <a-input style="width: 300px" v-model:value="queryForm.nickname" placeholder="请输入姓名"/>
        </a-form-item>
        <a-form-item label="账号" class="smart-query-form-item">
          <a-input style="width: 300px" v-model:value="queryForm.username" placeholder="请输入账号"/>
        </a-form-item>
        <a-form-item label="状态" class="smart-query-form-item">
          <SmartEnumSelect width="300px" enum-name="STATUS_ENUM" v-model:value="queryForm.userStatus"
                           placeholder="请选择状态"/>
        </a-form-item>
        <a-form-item v-if="!configData || configData === '1'" label="所属机构" class="smart-query-form-item">
          <a-tree-select
              show-search
              v-model:value="queryForm.deptId"
              style="width: 300px"
              :dropdown-style="{ maxHeight: '400px', overflow: 'auto' }"
              placeholder="请选择所属机构"
              allow-clear
              tree-node-filter-prop="deptName"
              :field-names="{ label: 'deptName', value: 'id' }"
              :tree-data="deptData"
          />
        </a-form-item>
        <a-form-item class="smart-query-form-item smart-margin-left10">
          <a-button-group>
            <a-button type="primary" @click="toFirstPage">
              <template #icon>
                <SearchOutlined/>
              </template>
              查询
            </a-button>
            <a-button @click="resetQuery">
              <template #icon>
                <ReloadOutlined/>
              </template>
              重置
            </a-button>
          </a-button-group>
        </a-form-item>
      </a-row>
    </a-form>
    <a-card size="small" :bordered="false" :hoverable="true">
      <a-row class="smart-table-btn-block">
        <div class="smart-table-operate-block">
          <a-button v-privilege="'user:add'" type="primary" @click="showDrawer">
            <template #icon>
              <PlusOutlined/>
            </template>
            新增
          </a-button>
          <a-button v-privilege="'user:delete'" type="primary" danger @click="batchDelete" :disabled="!hasSelected">
            <template #icon>
              <DeleteOutlined/>
            </template>
            批量删除
          </a-button>
          <a-button v-privilege="'user:export'" type="primary" ghost @click="exportData">
            <template #icon>
              <ExportOutlined/>
            </template>
            导出
          </a-button>
          <a-button v-privilege="'user:import'" type="primary" danger ghost @click="openImportModal">
            <template #icon>
              <ImportOutlined/>
            </template>
            导入
          </a-button>
        </div>
        <div class="smart-table-setting-block">
          <TableOperator v-model="columns" :refresh="query"/>
        </div>
      </a-row>
      <a-table
          :row-selection="{ selectedRowKeys: selectedRowKeys, onChange: onSelectChange }"
          size="small"
          :defaultExpandAllRows="true"
          :dataSource="tableData"
          bordered
          :columns="columns"
          :loading="tableLoading"
          rowKey="id"
          :pagination="false"
          @change="(pagination, filters, sorter) => $sorterChange(() => query(), sorter, queryForm, columns)"
      >
        <template #bodyCell="{ text, record, column }">
          <template v-if="column.dataIndex === 'avatar'">
            <div v-if="text">
              <a-image
                  :width="60"
                  :height="60"
                  v-for="(item,index) in text.split(',')"
                  :key="index"
                  :src="item"
                  fallback="data:image/png;base64,"
              />
            </div>
            <div v-else>
              <a-image
                  :width="60"
                  :height="60"
                  src="data:image/png;base64,"
              />
            </div>
          </template>
          <template v-if="column.dataIndex === 'identityInfo'">
            <div v-if="text != null">
              <div v-for="(item, index) in text.split(',')" :key="index">
                {{ item }}
              </div>
            </div>
            <div v-else-if="record.roleNames">
              <div v-for="(item, index) in record.roleNames.split(',')" :key="index">
                {{ item }}
              </div>
            </div>
          </template>
          <template v-if="column.dataIndex === 'userStatus'">
            <a-switch @change="val => updateStatus(record,val)" :checked="text" checked-value="1" un-checked-value="0"
                      checked-children="启用" un-checked-children="停用"/>
          </template>
          <template v-if="column.dataIndex === 'operate'">
            <div class="smart-table-operate">
              <a-button type="link" size="small" @click="showDrawer(record, true)">查看</a-button>
              <a-button v-privilege="'user:update'" type="link" size="small" @click="showDrawer(record)">编辑</a-button>
              <a-button v-privilege="'user:delete'" danger type="link" @click="singleDelete(record)">删除</a-button>
            </div>
          </template>
        </template>
      </a-table>
      <div class="smart-query-table-page">
        <a-pagination
            showSizeChanger
            showQuickJumper
            show-less-items
            :pageSizeOptions="PAGE_SIZE_OPTIONS"
            :defaultPageSize="queryForm.size"
            v-model:current="queryForm.current"
            v-model:pageSize="queryForm.size"
            :total="total"
            @change="query"
            @showSizeChange="query"
            :show-total="() => `共${total}条`"
        />
      </div>
    </a-card>
    <OperateModal ref="operateModal" @reloadList="query"/>
    <ImportData ref="importData" import-url="/system/user/import" @downloadTemplate="downloadTemplate" @finish="query"/>
  </div>
</template>
<script setup>
import {ExclamationCircleOutlined} from '@ant-design/icons-vue';
import {message, Modal} from 'ant-design-vue';
import {computed, createVNode, onMounted, reactive, ref} from 'vue';
import OperateModal from './components/operate-modal.vue';
import {columns} from './columns.js';
import {userApi} from '/@/api/system/user-api.js';
import SmartEnumSelect from '/@/components/framework/smart-enum-select/index.vue';
import {SmartLoading} from '/@/components/framework/smart-loading';
import {smartSentry} from '/@/lib/smart-sentry';
import TableOperator from '/@/components/support/table-operator/index.vue';
import {PAGE_SIZE_OPTIONS} from "/@/constants/common-const.js";
import {deptApi} from "/@/api/system/dept-api.js";
import {postApi} from "/@/api/system/post-api.js";
import {roleApi} from "/@/api/system/role-api.js";
import {exportFile} from "/@/utils/exportFile.js";
import ImportData from "/@/components/import-data/index.vue";
import {configApi} from "/@/api/system/config-api.js";
// ------------------------ 查询表单 ------------------------
const queryFormState = {
  userStatus: null,
  nickname: null,
  username: null,
  deptId: null,
  current: 1,
  size: 10,
  // 排序字段
  sortField: null,
  // 排序规则
  sortOrder: null
};
const queryForm = reactive({...queryFormState});

// ------------------------ table表格数据和查询方法 ------------------------
const tableLoading = ref(false);
const tableData = ref([]);
const total = ref(0);
const deptData = ref([])

// 重置
function resetQuery() {
  Object.assign(queryForm, queryFormState);
  columns.value.forEach(item => item.sortOrder = null);
  query();
}
// 回到第一页
function toFirstPage() {
  Object.assign(queryForm, { current: 1 });
  query();
}
// 查询
async function query() {
  try {
    tableLoading.value = true;
    let res = await userApi.userPage(queryForm);
    tableData.value = res.data.records;
    total.value = res.data.total;
  } catch (e) {
    smartSentry.captureError(e);
  } finally {
    tableLoading.value = false;
  }
}

// Mounted方法
onMounted(query);
onMounted(getDeptData);

// -------------- 多选操作 --------------
const selectedRowKeys = ref([]);
let selectedRows = [];
const hasSelected = computed(() => selectedRowKeys.value.length > 0);

// 选中触发
function onSelectChange(keyArray, selectRows) {
  selectedRowKeys.value = keyArray;
  selectedRows = selectRows;
}

// -------------- 删除操作 --------------
function singleDelete(record) {
  confirmBatchDelete([record]);
}

function batchDelete() {
  confirmBatchDelete(selectedRows);
}

function confirmBatchDelete(array) {
  Modal.confirm({
    title: '请确认是否删除?',
    icon: createVNode(ExclamationCircleOutlined),
    okText: '删除',
    okType: 'danger',
    onOk() {
      const deleteIds = array.map((e) => e.id);
      requestBatchDelete({deleteIds: deleteIds});
    },
    cancelText: '取消',
    onCancel() {
    },
  });

  async function requestBatchDelete(params) {
    SmartLoading.show();
    try {
      await userApi.deleteUser(params);
      // 清除选中
      selectedRowKeys.value = selectedRowKeys.value.filter(x => params.deleteIds.indexOf(x) === -1);
      selectedRows = [];
      message.success('删除成功!');
      await query();
    } catch (e) {
      smartSentry.captureError(e);
    } finally {
      SmartLoading.hide();
    }
  }
}

// -------------- 修改状态 --------------
async function updateStatus(record, val) {
  try {
    await userApi.updateUserStatus({id: record.id, userStatus: val})
    record.userStatus = val
  } catch (e) {
    smartSentry.captureError(e);
  } finally {
    tableLoading.value = false;
  }
}

// -------------- 新增、修改 右侧抽屉 --------------
const operateModal = ref();
const configData = ref('1');

async function getConfigData() {
  const configRes = await configApi.getConfigByKeys({keys: 'has_dept_post'});
  operateModal.value.configData = configRes.data.has_dept_post
  configData.value = configRes.data.has_dept_post
}
async function showDrawer(rowData, bool) {
  SmartLoading.show()
  let data = rowData
  if (data.id) {
    const res = await userApi.getUser({id: data.id})
    data = res.data
    if (data.identityList) {
      data.identityList.forEach((identity) => {
        if (identity.deptAncestors) {
          const arr = identity.deptAncestors.split(',')
          arr.shift()
          arr.push(identity.deptId)
          identity.deptAncestors = arr

        }
      })
    }
  }

  const deptRes = await deptApi.deptTree({status: '1'});
  operateModal.value.deptData = deptRes.data
  const postRes = await postApi.postList({status: '1'})
  operateModal.value.postData = postRes.data
  const roleRes = await roleApi.roleList({status: '1'})
  operateModal.value.roleData = roleRes.data
  operateModal.value.showDrawer(data, bool);
  SmartLoading.hide()
}

// 获取组织机构树
async function getDeptData() {
  const deptRes = await deptApi.deptTree();
  deptData.value = deptRes.data
}

// -------------- 导入导出 --------------
// 导出
async function exportData() {
  try {
    tableLoading.value = true;
    let params = {};
    Object.assign(params, queryForm);
    if (selectedRowKeys.value.length > 0) {
      params = {...params, selectIds: selectedRowKeys.value};
    }
    let res = await userApi.exportUserData(params);
    message.success('操作成功');
    exportFile(res.data, res.headers);
  } catch (e) {
    smartSentry.captureError(e);
  } finally {
    tableLoading.value = false;
  }
}

// 打开导入弹窗
const importData = ref();

function openImportModal() {
  importData.value.onOpen()
}

// 下载导入数据模板
async function downloadTemplate() {
  try {
    tableLoading.value = true;
    let res = await userApi.downloadUserTemplate();
    message.success('操作成功');
    exportFile(res.data, res.headers);
  } catch (e) {
    smartSentry.captureError(e);
  } finally {
    tableLoading.value = false;
  }
}

function init() {
  getConfigData()
}

init()
</script>
